package selenium;

public class Loopexamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// for loop initialization.. condition increment/decrement
		
		for (int i = 1; i < 5; i++) {
			System.out.println("I will do it");
		}
	
//		while  condition  is entry controlled loop
		int i=0;
		while (i<5) {
			System.out.println("Duck");
			i++;
		}
		
//		do while  is exit controlled loop
		int j=2;
		do {
			System.out.println("i will do it");
			j++;
		} while (j<=6);
		
		
	}

}
